#include "iccTagSignature.h"using namespace std;USING_ICC_LIB_NAMESPACE//--------------------------------------------------------------------iccTagSignature& iccTagSignature::operator=(const iccTagSignature& inTag){	if(this != &inTag)	{		*(iccTag*)this = *(iccTag*)&inTag;		m_signature = inTag.m_signature;	}	return *this;}//--------------------------------------------------------------------unsigned long iccTagSignature::GetSizeSelf(void) const{	return	sizeof m_signature;}//--------------------------------------------------------------------iccError iccTagSignature::LoadSelf(istream& inStream,unsigned long inSize){	long	insize	= inSize;		inStream >> m_signature;	insize -= sizeof m_signature;		if(insize != 0)		return iccErrorRead;		return iccErrorNone;}//--------------------------------------------------------------------iccError iccTagSignature::SaveSelf(ostream& outStream,unsigned long& outSize) const{	outStream << m_signature;	outSize += sizeof m_signature;	return iccErrorNone;}