#include "iccTagArrayNumberBasic.h"using namespace std;USING_ICC_LIB_NAMESPACE//--------------------------------------------------------------------template <typename T>iccTagArrayNumberBasic<T>& iccTagArrayNumberBasic<T>::operator=(const iccTagArrayNumberBasic<T>& inTag){	if(this != &inTag)	{		m_numbers = inTag.m_numbers;	}	return *this;}//--------------------------------------------------------------------template <typename T>unsigned long iccTagArrayNumberBasic<T>::GetSizeSelf(void) const{	return	sizeof(T)*m_numbers.size();}//--------------------------------------------------------------------template <typename T>iccError iccTagArrayNumberBasic<T>::LoadSelf(istream& inStream,unsigned long inSize){	T		number;	long	size = inSize;	while(	0 < size			&& !inStream.eof() )	{		inStream >> number;		m_numbers.push_back(number);		size -= sizeof(T);	}		return iccErrorNone;}//--------------------------------------------------------------------template <typename T>iccError iccTagArrayNumberBasic<T>::SaveSelf(ostream& outStream,unsigned long& outSize) const{	unsigned long	i		= 0,					imax	= 0;	for(i=0,imax=m_numbers.size(); i<imax; i++)	{		outStream << m_numbers[i];		outSize += sizeof(T);	}	return iccErrorNone;}